import React, { useState } from 'react';

function Board(props){

  const[squares, setSquares] = useState(Array(9).fill(null))

  function handleClick(id) {
    const squaresLocal = squares.slice()
    squaresLocal[id] = 'x'
  }

  return(
    <div className="gameBoard">
      <div className="status text-center">
        Next Player: {props.player}
      </div>
      <div className="gameRow">
        <Square value={squares[0]} id={0} handleClick={handleClick}/>
        <Square value={squares[1]} id={1} handleClick={handleClick}/>
        <Square value={squares[2]} id={2} handleClick={handleClick}/>
      </div>
      <div className="gameRow">
        <Square value={squares[3]} id={3} handleClick={handleClick}/>
        <Square value={squares[4]} id={4} handleClick={handleClick}/>
        <Square value={squares[5]} id={5} handleClick={handleClick}/>
      </div>
      <div className="gameRow">
        <Square value={squares[6]} id={6} handleClick={handleClick}/>
        <Square value={squares[7]} id={7} handleClick={handleClick}/>
        <Square value={squares[8]} id={8} handleClick={handleClick}/>
      </div>
    </div>
  )
}

function Square(props) {
  return(
    <button 
    onClick={() => null}
    className="w-[15vh] h-[15vh] border-[1px] rounded-sm m-1
    border-white transition hover:bg-green-400 active:bg-green-600">
      {props.value}
    </button>
  )
}



function App() {
  return (
    <>
    <div className="bg-slate-600 text-white h-screen flex justify-center items-center">
      <Board player="x"></Board>
    </div>

    </>
  );
}

export default App;
